CREATE TABLE `smartcard_district_relation` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `smartCardId` varchar(64) NOT NULL COMMENT '',
  `statusCode` varchar(10) NOT NULL COMMENT '',
  `districtCode` varchar(100) NOT NULL COMMENT '',
  `appType` varchar(5) NOT NULL COMMENT '01',
  `factoryName` varchar(256) NOT NULL COMMENT '',
  `createTime` datetime NOT NULL COMMENT '/',
  `queryEndTime` datetime DEFAULT NULL,
  `queryStartTime` datetime DEFAULT NULL,
  `sn` varchar(64) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `idx_smartCardId` (`smartCardId`)
) ENGINE=InnoDB AUTO_INCREMENT=1462231 DEFAULT CHARSET=utf8 COMMENT='';
