            marshaller.setProperty(Marshaller.JAXB_FORMATTED_OUTPUT, true);
            marshaller.marshal(t, outs);
            xmlString = new String(outs.toByteArray(), ENCODING);
        }
        catch (Exception e)
        {
            e.printStackTrace();
            throw new AAAException("405");
        }
        finally
        {
            try
            {
                if (outs != null)
